import React, { useState } from "react";
import { MDBDataTable } from "mdbreact";
import Aux from "../Aux/Aux";
import ViewModal from './Modals/ViewModal';
import EditModal from './Modals/EditModal';
const Buttons = props => {
  const [show, setShow] = useState({ edit: false, view: false });
  const handleClose = () => setShow({ edit: false });
  const handleShow = () => setShow({ edit: true });
  const viewHandleClose = () => setShow({ view: false });
  const viewHandleShow = () => setShow({ view: true });

  return (
    <Aux style={{ display: "inline-block" }}>
      <button className="btn btn-primary btn-sm" onClick={handleShow}>
        Edit
      </button>
      <button className="btn btn-info btn-sm" onClick={viewHandleShow}>
        View
      </button>
    </Aux>
  );
};

const OrderDataTable = props => {
  let buttonized = [...props.data];
  buttonized.map((el, index) => {
    el["id"] = index;
    el["action"] = <Buttons id={index}></Buttons>;
    return el;
  });

  const data = {
    columns: [
      {
        label: "Action",
        field: "action",
        sort: "asc",
        width: 400
      },
      {
        label: "ORDERSTATUS",
        field: "ORDERSTATUS",
        sort: "asc"
        // width: 150
      },
      {
        label: "Status",
        field: "status",
        sort: "asc"
      },
      {
        label: "PAAccountStatus",
        field: "PAAccountStatus",
        sort: "asc"
      },
      {
        label: "StckID",
        field: "stock_id",
        sort: "asc"
      },
      {
        label: "PurAgmt",
        field: "Pur_Agmt",
        sort: "asc"
      },
      {
        label: "PurDt",
        field: "pur_dt",
        sort: "asc"
      },
      {
        label: "SalesOrderNo",
        field: "SalesOrderNo",
        sort: "asc"
      },
      {
        label: "SalesOrderDate",
        field: "SalesOrderDate",
        sort: "asc"
      },
      {
        label: "SalesType",
        field: "SalesType",
        sort: "asc"
      },
      {
        label: "Entrystaff",
        field: "entrystaff",
        sort: "asc"
      },
      {
        label: "PurType",
        field: "pur_type",
        sort: "asc"
      },
      {
        label: "GST",
        field: "gst_mtd",
        sort: "asc"
      },
      {
        label: "Reg no",
        field: "reg_no",
        sort: "asc"
      },
      {
        label: "rev",
        field: "rev",
        sort: "asc"
      }
    ],
    rows: buttonized
  };

  return <MDBDataTable striped bordered responsive hover data={data} />;
};

export default OrderDataTable;
