import React, { Component } from "react";
import Modal from "react-bootstrap/Modal";
import Button from "react-bootstrap/Button";
import Wrapper from "../../../hoc/Wrapper";
import axios from "../../../axios";
export default class ViewModal extends Component {
  constructor(props) {
    super(props);
    this.state = {
      edit: false,
      view: false,
      viewInfo: []
    };

    this.viewHandleClose = this.viewHandleClose.bind(this);
    this.viewHandleShow = this.viewHandleShow.bind(this);
  }
  viewHandleClose = () => this.setState({ view: false });
  viewHandleShow = () => {
    axios
      .get("/api/values?id=" + this.props.code_id)
      .then(response => {
        console.log(response.data);
        this.state.viewInfo = response.data;
        if (this.state.viewInfo) this.setState({ view: true });
      })
      .catch(err => console.log(err));
  };
  render() {
    return (
      <Wrapper>
        <button className="btn btn-info btn-sm" onClick={this.viewHandleShow}>
          Show
        </button>
        <Modal
          size="xl"
          show={this.state.view}
          onHide={this.viewHandleClose}
          animation={false}
        >
          <Modal.Header closeButton>
            <Modal.Title>Modal heading</Modal.Title>
          </Modal.Header>
          <Modal.Body>
            <form>
              <div className="col-4">
                <div className="form-group">
                  <label>Make</label>
                  <input
                    type="text"
                    name="make"
                    className="form-control form-control-sm"
                  />
                </div>
                <div className="form-group">
                  <label>Make</label>
                  <input
                    type="text"
                    name="make"
                    className="form-control form-control-sm"
                  />
                </div>
                <div className="form-group">
                  <label>Make</label>
                  <input
                    type="text"
                    name="make"
                    className="form-control form-control-sm"
                  />
                </div>
                <div className="form-group">
                  <label>Make</label>
                  <input
                    type="text"
                    name="make"
                    className="form-control form-control-sm"
                  />
                </div>
                <div className="form-group">
                  <label>Example textarea</label>
                  <textarea
                    class="form-control"
                    rows="3"
                  ></textarea>
                </div>
              </div>
            </form>
          </Modal.Body>
          <Modal.Footer>
            <Button variant="secondary" onClick={this.viewHandleClose}>
              Close
            </Button>
            <Button variant="primary" onClick={this.viewHandleClose}>
              Save Changes
            </Button>
          </Modal.Footer>
        </Modal>
      </Wrapper>
    );
  }
}
