import React, { Component } from "react";
import Modal from "react-bootstrap/Modal";
import Button from "react-bootstrap/Button";
import Wrapper from "../../../hoc/Wrapper";
import axios from "../../../axios";

// had to change it to ClassComponent because I've to use lifecycle methods;
export default class ViewModal extends Component {
  constructor(props) {
    super(props);
    this.state = {
      edit: false,
      view: false,
      viewInfo: []
    };

    // this.viewHandleClose = this.viewHandleClose.bind(this);
    // this.viewHandleShow = this.viewHandleShow.bind(this);
  }
  //   viewHandleClose = () => this.setState({ view: false });
    viewHandleShow = () => {
      axios
        .get("/api/values?id=" + this.props.code_id)
        .then(response => {
          this.setState({ viewInfo: response.data });
          this.setState({ view: true });
        })
        .catch(err => console.table(err));
      console.log(this.state.viewInfo);
    };

  render() {
    return (
      <Wrapper>
        <Modal
          size="xl"
          show={this.state.view}
          onHide={this.viewHandleClose}
          animation={false}
        >
          <Modal.Header closeButton>
            <Modal.Title>Show</Modal.Title>
          </Modal.Header>
          <Modal.Body>{this.props.code_id}</Modal.Body>
          <Modal.Footer>
            <Button variant="secondary" onClick={this.viewHandleClose}>
              Close
            </Button>
            <Button variant="primary" onClick={this.viewHandleClose}>
              Save Changes
            </Button>
          </Modal.Footer>
        </Modal>
      </Wrapper>
    );
  }
}
