import React, { Component } from "react";
import Modal from "react-bootstrap/Modal";
import Button from "react-bootstrap/Button";
import Wrapper from "../../../hoc/Wrapper";
// had to change it to ClassComponent because I've to use lifecycle methods;

export default class ViewModal extends Component {
  constructor(props) {
    super(props);
    this.state = {
      edit: false,
      view: false
    };
    const viewHandleClose = () => setShow({ view: false });
    const viewHandleShow = () => setShow({ view: true });
  }

  render() {
    return (
      <Wrapper>
        <button className="btn btn-primary btn-sm" onClick={viewHandleShow}>
          Show
        </button>
        <Modal
          size="xl"
          show={show.view}
          onHide={viewHandleClose}
          animation={false}
        >
          <Modal.Header closeButton>
            <Modal.Title>Modal heading</Modal.Title>
          </Modal.Header>
          <Modal.Body>{props.code_id}</Modal.Body>
          <Modal.Footer>
            <Button variant="secondary" onClick={viewHandleClose}>
              Close
            </Button>
            <Button variant="primary" onClick={viewHandleClose}>
              Save Changes
            </Button>
          </Modal.Footer>
        </Modal>
      </Wrapper>
    );
  }
}
