import React, { useState } from "react";
import Modal from "react-bootstrap/Modal";
import Button from "react-bootstrap/Button";
import Wrapper from "../../../hoc/Wrapper";

const ViewModal = props => {
  const [show, setShow] = useState({ edit: false, view: false });
  const viewHandleClose = () => setShow({ view: false });
  const viewHandleShow = () => setShow({ view: true });
  return (
    <Wrapper>
      <button className="btn btn-primary btn-sm" onClick={viewHandleShow}>
        View
      </button>
      <Modal
        size="xl"
        show={show.view}
        onHide={viewHandleClose}
        animation={false}
      >
        <Modal.Header closeButton>
          <Modal.Title>Modal heading</Modal.Title>
        </Modal.Header>
        <Modal.Body>View...</Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={viewHandleClose}>
            Close
          </Button>
          <Button variant="primary" onClick={viewHandleClose}>
            Save Changes
          </Button>
        </Modal.Footer>
      </Modal>
    </Wrapper>
  );
};

export default ViewModal;
