import React, { useState } from "react";
import Modal from "react-bootstrap/Modal";
import Button from "react-bootstrap/Button";
import Wrapper from "../../../hoc/Wrapper";

const EditModal = props => {
  const [show, setShow] = useState({ edit: false });
  const handleClose = () => setShow({ edit: false });
  const handleShow = () => setShow({ edit: true });

  return (
    <Wrapper>
      <button className="btn btn-info btn-sm" onClick={handleShow}>
        Edit
      </button>
      <Modal size="xl" show={show.edit} onHide={handleClose} animation={false}>
        <Modal.Header closeButton>
          <Modal.Title>Modal heading</Modal.Title>
        </Modal.Header>
        <Modal.Body>Edit...</Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleClose}>
            Close
          </Button>
          <Button variant="primary" onClick={handleClose}>
            Save Changes
          </Button>
        </Modal.Footer>
      </Modal>
    </Wrapper>
  );
};

export default EditModal;
