import React, { Component } from "react";
import axios from "../../../axios";
export default class ViewModal extends Component {
  constructor(props) {
    super(props);
    this.state = {
      stock_id: []
    };
  }

  componentDidMount() {
    axios
      .get("/api/values/purchaseDetails/" + this.props.match.params.id)
      .then(response => {
        if (response.data) this.setState({ stock_id: response.data });
      })
      .catch(error => console.log(error));
  }

  render() {
    return (
      <>
        <h3>Stock: {this.state.stock_id.Pur_Agmt}</h3>

        <form>
          <div className="row">
            <div className="col-4">
              <div className="form-group">
                <label>Stock id</label>
                <input type="text" className="form-control form-control-sm" value={this.state.stock_id.Stock_id}/>
              </div>
            </div>
          </div>
        </form>
      </>
    );
  }
}
