import React, { Component } from "react";
import axios from "../../../axios";
export default class ViewModal extends Component {
  constructor(props) {
    super(props);
    this.state = {
      stock_data: {Stock_id:''}

    };
    this.submitHandler = this.submitHandler.bind(this);
    this.changeHandler = this.changeHandler.bind(this);
  }

  componentDidMount() {
    axios
      .get("/api/values/purchaseDetails/" + this.props.match.params.id)
      .then(response => {
        if (response.data) this.setState({ stock_data: response.data });
        console.log(response.data);
      })
      .catch(error => console.log(error));
  }

  submitHandler(e) {
    e.preventDefault();
    this.setState({
      stock_id: e.target.stock_id
    });
  }

  changeHandler(e) {
    const value  = e.target;
    const name   = e.target.name;
    this.setState(state => ({
      stock_data: {
        ...state.stock_data,
        [name]: value
      }
    }));
  }

  render() {
    return (
      <>
        <h3>Stock: {this.state.stock_data.Pur_Agmt}</h3>
        {this.state.stock_data != [] ? (
          <form onSubmit={this.submitHandler}>
            <div className="row">
              <div className="col-4">
                <div className="form-group">
                  <label>Stock id</label>
                  <input
                    type="text"
                    name="Stock_id"
                    className="form-control form-control-sm"
                    onChange={this.changeHandler}
                    value={this.state.stock_data.Stock_id}
                  />
                </div>
              </div>
            </div>
          </form>
        ) : (
          "Loading..."
        )}
      </>
    );
  }
}
