import React, { Component } from "react";
import Modal from "react-bootstrap/Modal";
import Button from "react-bootstrap/Button";
import Wrapper from "../../../hoc/Wrapper";
// had to change it to ClassComponent because I've to use lifecycle methods;

export default class ViewModal extends Component {
  constructor(props) {
    super(props);
    this.state = {
      edit: false,
      view: false
    };

    this.viewHandleClose = this.viewHandleClose.bind(this);
    this.viewHandleShow = this.viewHandleShow.bind(this);
  }
  viewHandleClose = () => this.setState({ view: false });
  viewHandleShow = () => this.setState({ view: true });
  render() {
    return (
      <Wrapper>
        <button className="btn btn-primary btn-sm" onClick={this.viewHandleShow}>
          Show
        </button>
        <Modal
          size="xl"
          show={this.state.view}
          onHide={this.viewHandleClose}
          animation={false}
        >
          <Modal.Header closeButton>
            <Modal.Title>Modal heading</Modal.Title>
          </Modal.Header>
          <Modal.Body>{this.props.code_id}</Modal.Body>
          <Modal.Footer>
            <Button variant="secondary" onClick={this.viewHandleClose}>
              Close
            </Button>
            <Button variant="primary" onClick={viewHandleClose}>
              Save Changes
            </Button>
          </Modal.Footer>
        </Modal>
      </Wrapper>
    );
  }
}
