import React, { Component } from "react";
import axios from "../../axios";
import OrderDataTable from "../../components/OrderDataTable/OrderDataTable";
// import Spinner from "react-bootstrap/Spinner";
import { PushSpinner, MagicSpinner } from "react-spinners-kit";
export default class OrderStatus extends Component {
  constructor(props) {
    super(props);
    this.state = {
      orderstatus: [],
      loading: true
    };
  }

  // Fetch data from API after the component have been loaded;
  componentDidMount() {
    axios
      .get("api/values/purchaseList")
      .then(response => {
        this.setState({
          orderstatus: response.data,
          loading: false
        });
      })
      .catch(err => {
        console.log("Server Down" + err);
      });
  }

  render() {
    return (
      <>
        {this.state.loading ? (
          <div className="text-center">
            <MagicSpinner
              size={100}
              color="#686769"
              loading={this.state.loading}
            />
          </div>
        ) : (
          <OrderDataTable data={this.state.orderstatus} />
        )}
      </>
    );
  }
}
